mapper.select:
SELECT L.ID , 'post' AS TYPE , P.CONTENT , P.CREATED_AT FROM LIKES L JOIN POST P ON L.POST_ID = P.ID WHERE L.USER_ID = #{userId}
UNION ALL
SELECT L.ID , 'comment' AS TYPE , C.CONTENT , C.CREATED_AT FROM LIKES L JOIN COMMENT C ON L.COMMENT_ID = C.ID WHERE L.USER_ID = #{userId}
UNION ALL
SELECT L.ID , 'board' AS TYPE , B.TITLE AS CONTENT , B.CREATED_AT FROM LIKES L JOIN ADVICE_BOARD B ON L.BOARD_ID = B.ID WHERE L.USER_ID = #{userId}
